{
  "runtimeId": "{{atomId}}",
  "generalSettings": {
    "@type": "GeneralSettings",
    "enabled": true,
    "observabilityEndpoint": {
      "@type": "ObservabilityEndpoint",
      "url": "http://alloy:4317",
      "authentication": {
        "@type": "ObservabilityEndpointAuthentication",
        "authType": "NONE"
      }
    }
  },
  "logSettings": {
    "@type": "LogSettings",
    "enabled": true, 
    "logTimeoutSeconds": 30,
    "logBatchSize": 512,
    "logFlushInterval": 1000,
    "logMaxQueueSize": 2048,
    "logFilterEnabled": false
  },
  "metricSettings": {
    "@type": "MetricSettings",
    "enabled": true,
    "overrideGeneralObservabilityEndpoint": false,
    "metricTimeoutSeconds": 30,
    "metricIntervalSeconds": 30
  },
  "traceSettings": {
    "@type": "TraceSettings",
    "enabled": true,
    "overrideGeneralObservabilityEndpoint": false,
    "traceBatchSize": 0,
    "traceFlushInterval": 100,
    "traceTimeoutSeconds": 0,
    "traceMaxQueueSize": 0,
    "processFilterEnabled": false
  },
  "shouldRestartPlugin": true
}
